// SearchComponent.tsx
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Update import
import { searchNpmPackages } from '../services/npmApi';
import { useFavorites } from '../context/FavoritesContext';

const SearchComponent: React.FC = () => {
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const { addToFavorites } = useFavorites();
  const navigate = useNavigate(); // Update to useNavigate

  const handleSearch = async () => {
    try {
      const result = await searchNpmPackages(query);
      setSearchResults(result.results);
    } catch (error) {
      // Handle error appropriately
    }
  };

  const handleAddToFavorites = (packageName: string) => {
    const reason = prompt(`Why is ${packageName} your favorite?`);
    if (reason) {
      addToFavorites(packageName, reason);
    }
  };

  const navigateToFavorites = () => {
    navigate('/favorites'); // Update navigation
  };

  return (
    <div>
      <div>
        <Link to="/favorites">
          <button>Go to Favorites</button>
        </Link>
      </div>

      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for NPM packages"
      />
      <button onClick={handleSearch}>Search</button>

      <div>
        <h2>Search Results:</h2>
        <ul>
          {searchResults.map((resultPackage: any) => (
            <li key={resultPackage.package.name}>
              {resultPackage.package.name}{' '}
              <button onClick={() => handleAddToFavorites(resultPackage.package.name)}>
                Add to Favorites
              </button>
            </li>
          ))}
        </ul>
      </div>

      <button onClick={navigateToFavorites}>Go to Favorites</button>
    </div>
  );
};

export default SearchComponent;
