// components/SearchComponent.tsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { searchNpmPackages } from '../services/npmApi';
import { useFavorites } from '../context/FavoritesContext';

const SearchComponent: React.FC = () => {
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const { addToFavorites } = useFavorites();

  const handleSearch = async () => {
    try {
      const result = await searchNpmPackages(query);
      setSearchResults(result.results);
    } catch (error) {
      // Handle error appropriately
    }
  };

  const handleAddToFavorites = (packageName: string) => {
    const reason = prompt(`Why is ${packageName} your favorite?`);
    if (reason) {
      addToFavorites(packageName, reason);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-gray-100 rounded-md shadow-md">
      <input
        className="w-full border-2 border-gray-300 p-3 mb-4 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for NPM packages"
      />
      <button
        className="bg-blue-500 text-white p-3 rounded-md hover:bg-blue-700 transition duration-300"
        onClick={handleSearch}
      >
        Search
      </button>

      <div className="mt-8">
        <h2 className="text-2xl font-bold mb-4">Search Results:</h2>
        <ul>
          {searchResults.map((pkg: any) => (
            <li key={pkg.package.name} className="mb-4">
              <div className="flex justify-between items-center">
                <span className="text-lg font-semibold">{pkg.package.name}</span>
                <button
                  className="bg-green-500 text-white p-2 rounded-md hover:bg-green-700 transition duration-300"
                  onClick={() => handleAddToFavorites(pkg.package.name)}
                >
                  Add to Favorites
                </button>
              </div>
              <p className="text-gray-600">{pkg.package.description}</p>
            </li>
          ))}
        </ul>
      </div>

      <div className="mt-8">
        {/* Navigation button to view favorites */}
        <Link to="/favorites">
          <button className="bg-indigo-500 text-white p-3 rounded-md hover:bg-indigo-700 transition duration-300">
            View Favorites
          </button>
        </Link>
      </div>
    </div>
  );
};

export default SearchComponent;
