// components/SearchComponent.tsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { searchNpmPackages } from '../services/npmApi';
import { useFavorites } from '../context/FavoritesContext';
import { FaSearch, FaHeart, FaArrowRight } from 'react-icons/fa'; // Import icons

const SearchComponent: React.FC = () => {
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const { addToFavorites } = useFavorites();

  const handleSearch = async () => {
    try {
      const result = await searchNpmPackages(query);
      setSearchResults(result.results);
    } catch (error) {
      // Handle error appropriately
    }
  };

  const handleAddToFavorites = (packageName: string) => {
    const reason = prompt(`Why is ${packageName} your favorite?`);
    if (reason) {
      addToFavorites(packageName, reason);
    }
  };

  return (
    <div className="max-w-screen-lg mx-auto p-6 bg-white rounded-md shadow-md">
      {/* Navigation Bar */}
      <nav className="flex justify-between items-center mb-6 bg-indigo-600 text-white p-4 rounded-md">
        <div>
          <h1 className="text-4xl font-extrabold">NPM Packages</h1>
          <p className="text-lg">Discover and manage your favorite NPM packages</p>
        </div>
        <div>
          <Link to="/favorites">
            <button className="bg-white text-indigo-600 p-3 rounded-md hover:bg-indigo-700 transition duration-300">
              View Favorites <FaArrowRight />
            </button>
          </Link>
        </div>
      </nav>

      {/* Search Section */}
      <div className="flex items-center mb-6">
        <input
          className="w-full lg:w-120 border-2 border-gray-300 p-3 rounded-l-md focus:outline-none focus:ring-2 focus:ring-indigo-500 placeholder-gray-500"
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search for NPM packages"
        />
        <button
          className="bg-indigo-600 text-white p-3 rounded-r-md hover:bg-indigo-700 transition duration-300"
          onClick={handleSearch}
        >
          <FaSearch />
        </button>
      </div>

      {/* Search Results */}
      <div className="mt-8">
        <h2 className="text-3xl font-bold mb-4 text-indigo-600">Search Results:</h2>
        <ul>
          {searchResults.map((pkg: any) => (
            <li key={pkg.package.name} className="mb-6 bg-gray-100 p-6 rounded-md">
              <div className="flex justify-between items-center">
                <span className="text-xl font-semibold text-indigo-600">{pkg.package.name}</span>
                <button
                  className="bg-green-500 text-white p-3 rounded-md hover:bg-green-700 transition duration-300"
                  onClick={() => handleAddToFavorites(pkg.package.name)}
                >
                  <FaHeart /> Add to Favorites
                </button>
              </div>
              <p className="text-gray-600 mt-2">{pkg.package.description}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default SearchComponent;
