// components/Favorites.tsx
import React, { useState } from 'react';
import { useFavorites, Favorite } from '../context/FavoritesContext'; // Import Favorite
import { useNavigate } from 'react-router-dom';

const Favorites: React.FC = () => {
  const { favorites, removeFromFavorites } = useFavorites();
  const navigate = useNavigate();
  const [selectedFavorite, setSelectedFavorite] = useState<Favorite | null>(null);

  const handleRemoveClick = (packageName: string) => {
    const confirmation = window.confirm(`Are you sure you want to remove ${packageName}?`);
    if (confirmation) {
      removeFromFavorites(packageName);
    }
  };

  const handleViewClick = (packageName: string) => {
    const favorite = favorites.find((fav) => fav.packageName === packageName);
    if (favorite) {
      setSelectedFavorite(favorite);
    }
  };

  const handleGoToSearch = () => {
    navigate('/search');
  };

  return (
    <div className="container mx-auto mt-8 p-4">
      <h2 className="text-3xl font-bold mb-4">Favorites</h2>
      <ul className="space-y-4">
        {favorites.map((favorite, index) => (
          <li key={index} className="bg-white p-4 rounded-md shadow-md flex items-center justify-between">
            <span className="text-lg">{favorite.packageName}</span>
            <div>
              <button
                onClick={() => handleRemoveClick(favorite.packageName)}
                className="ml-4 px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-700"
              >
                Remove
              </button>
              <button
                onClick={() => handleViewClick(favorite.packageName)}
                className="ml-4 px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-700"
              >
                View
              </button>
            </div>
          </li>
        ))}
      </ul>

      {/* Button to navigate to SearchComponent */}
      <button
        onClick={handleGoToSearch}
        className="mt-8 px-6 py-3 bg-gray-600 text-white rounded-md hover:bg-gray-700"
      >
        Home
      </button>

      {/* Reason Card */}
      {selectedFavorite && (
        <div className="modal fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="modal-content bg-white p-8 rounded-md">
            <span
              className="close text-2xl cursor-pointer absolute top-2 right-4"
              onClick={() => setSelectedFavorite(null)}
            >
              &times;
            </span>
            <p className="text-lg">{selectedFavorite.reason}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Favorites;
