// components/SearchComponent.tsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { searchNpmPackages } from '../services/npmApi';
import { useFavorites } from '../context/FavoritesContext';

const SearchComponent: React.FC = () => {
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const { addToFavorites } = useFavorites();

  const handleSearch = async () => {
    try {
      const result = await searchNpmPackages(query);
      setSearchResults(result.results);
    } catch (error) {
      // Handle error appropriately
    }
  };

  const handleAddToFavorites = (packageName: string) => {
    const reason = prompt(`Why is ${packageName} your favorite?`);
    if (reason) {
      addToFavorites(packageName, reason);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <input
        className="w-full border p-2 mb-4"
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for NPM packages"
      />
      <button
        className="bg-blue-500 text-white p-2 rounded hover:bg-blue-700"
        onClick={handleSearch}
      >
        Search
      </button>

      <div className="mt-8">
        <h2 className="text-xl font-bold mb-4">Search Results:</h2>
        <ul>
          {searchResults.map((pkg: any) => (
            <li key={pkg.package.name} className="mb-2">
              {pkg.package.name}{' '}
              <button
                className="bg-green-500 text-white p-2 rounded hover:bg-green-700"
                onClick={() => handleAddToFavorites(pkg.package.name)}
              >
                Add to Favorites
              </button>
            </li>
          ))}
        </ul>
      </div>

      <div className="mt-8">
        {/* Navigation button to view favorites */}
        <Link to="/favorites">
          <button className="bg-indigo-500 text-white p-2 rounded hover:bg-indigo-700">
            View Favorites
          </button>
        </Link>
      </div>
    </div>
  );
};

export default SearchComponent;
