// context/FavoritesContext.tsx
import React, {
  createContext,
  useContext,
  useState,
  ReactNode,
  useReducer,
  Reducer,
  Dispatch,
  SetStateAction,
} from 'react';

export interface Favorite {
  packageName: string;
  reason: string;
}

interface FavoritesContextProps {
  favorites: Favorite[];
  dispatch: Dispatch<Action>;
  addToFavorites: (packageName: string, reason: string) => void;
  removeFromFavorites: (packageName: string) => void;
}

interface FavoritesProviderProps {
  children: ReactNode;
}

type Action =
  | { type: 'ADD_TO_FAVORITES'; payload: Favorite }
  | { type: 'REMOVE_FROM_FAVORITES'; payload: string };

const favoritesReducer: Reducer<Favorite[], Action> = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_FAVORITES':
      return [...state, action.payload];
    case 'REMOVE_FROM_FAVORITES':
      return state.filter((fav) => fav.packageName !== action.payload);
    default:
      return state;
  }
};

const FavoritesContext = createContext<FavoritesContextProps | undefined>(undefined);

export const FavoritesProvider: React.FC<FavoritesProviderProps> = ({ children }) => {
  const [favorites, dispatch] = useReducer(favoritesReducer, []);

  const addToFavorites = (packageName: string, reason: string) => {
    const newFavorite: Favorite = { packageName, reason };
    dispatch({ type: 'ADD_TO_FAVORITES', payload: newFavorite });
  };

  const removeFromFavorites = (packageName: string) => {
    dispatch({ type: 'REMOVE_FROM_FAVORITES', payload: packageName });
  };

  return (
    <FavoritesContext.Provider value={{ favorites, dispatch, addToFavorites, removeFromFavorites }}>
      {children}
    </FavoritesContext.Provider>
  );
};

export const useFavorites = () => {
  const context = useContext(FavoritesContext);
  if (!context) {
    throw new Error('useFavorites must be used within a FavoritesProvider');
  }
  return context;
};
