// components/Favorites.tsx
import React, { useState } from 'react';
import { useFavorites } from '../context/FavoritesContext';
import { useNavigate } from 'react-router-dom';

const Favorites: React.FC = () => {
  const { favorites, removeFromFavorites } = useFavorites();
  const navigate = useNavigate();
  const [selectedFavorite, setSelectedFavorite] = useState<string | null>(null);

  const handleRemoveClick = (packageName: string) => {
    const confirmation = window.confirm(`Are you sure you want to remove ${packageName}?`);
    if (confirmation) {
      removeFromFavorites(packageName);
    }
  };

  const handleViewClick = (packageName: string) => {
    const favorite = favorites.find((fav) => fav.packageName === packageName);
    if (favorite) {
      setSelectedFavorite(favorite.reason);
    }
  };

  const handleGoToSearch = () => {
    // Use the navigate function to go to the "/search" route
    navigate('/search');
  };

  return (
    <div>
      <h2>Favorites</h2>
      <ul>
        {favorites.map((favorite, index) => (
          <li key={index}>
            {favorite.packageName}{' '}
            <button onClick={() => handleRemoveClick(favorite.packageName)}>
              Remove
            </button>
            <button onClick={() => handleViewClick(favorite.packageName)}>
              View
            </button>
          </li>
        ))}
      </ul>

      {/* Button to navigate to SearchComponent */}
      <button onClick={handleGoToSearch}>Home</button>

      {/* Modal to show the reason */}
      {selectedFavorite && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setSelectedFavorite(null)}>
              &times;
            </span>
            <p>{selectedFavorite}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Favorites;
