 // components/SearchComponent.tsx
import React, { useState } from 'react';
import { searchNpmPackages } from '../services/npmApi';
import { useFavorites } from '../context/FavoritesContext';

const SearchComponent: React.FC = () => {
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const { addToFavorites } = useFavorites();

  const handleSearch = async () => {
    try {
      const result = await searchNpmPackages(query);
      setSearchResults(result.results);
    } catch (error) {
      // Handle error appropriately
    }
  };

  const handleAddToFavorites = (packageName: string) => {
    const reason = prompt(`Why is ${packageName} your favorite?`);
    if (reason) {
      addToFavorites(packageName, reason);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for NPM packages"
      />
      <button onClick={handleSearch}>Search</button>

      <div>
        <h2>Search Results:</h2>
        <ul>
          {searchResults.map((pkg: any) => (
            <li key={pkg.package.name}>
              {pkg.package.name}{' '}
              <button onClick={() => handleAddToFavorites(pkg.package.name)}>
                Add to Favorites
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default SearchComponent;
