// components/Favorites.tsx
import React, { useState } from 'react';
import { useFavorites } from '../context/FavoritesContext';
import { useNavigate } from 'react-router-dom';

const Favorites: React.FC = () => {
  const { favorites, removeFromFavorites } = useFavorites();
  const navigate = useNavigate();
  const [selectedFavorite, setSelectedFavorite] = useState<string | null>(null);

  const handleRemoveClick = (packageName: string) => {
    const confirmation = window.confirm(`Are you sure you want to remove ${packageName}?`);
    if (confirmation) {
      removeFromFavorites(packageName);
    }
  };

  const handleViewClick = (packageName: string) => {
    const favorite = favorites.find((fav) => fav.packageName === packageName);
    if (favorite) {
      setSelectedFavorite(favorite.reason);
    }
  };

  const handleGoToSearch = () => {
    navigate('/search');
  };

  return (
    <div className="max-w-screen-md mx-auto p-8 bg-white rounded-md shadow-md">
      <h2 className="text-3xl font-bold mb-4">Your Favorites</h2>
      <ul>
        {favorites.map((favorite, index) => (
          <li key={index} className="mb-4">
            <div className="bg-gray-100 p-6 rounded-md shadow-md flex justify-between items-center">
              <div>
                <span className="text-lg">{favorite.packageName}</span>
                <p className="text-gray-600 mt-2">{favorite.reason}</p>
              </div>
              <div className="space-x-4">
                <button
                  className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-700"
                  onClick={() => handleRemoveClick(favorite.packageName)}
                >
                  Remove
                </button>
                <button
                  className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700"
                  onClick={() => handleViewClick(favorite.packageName)}
                >
                  View
                </button>
              </div>
            </div>
          </li>
        ))}
      </ul>

      {/* Button to navigate to SearchComponent */}
      <button
        className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700 mt-4"
        onClick={handleGoToSearch}
      >
        Back to Search
      </button>

      {/* Modal to show the reason */}
      {selectedFavorite && (
        <div className="fixed inset-0 z-50 flex items-center justify-center">
          <div className="absolute inset-0 bg-black opacity-50" onClick={() => setSelectedFavorite(null)}></div>
          <div className="bg-white p-8 rounded-md">
            <span className="cursor-pointer absolute top-4 right-4 text-2xl" onClick={() => setSelectedFavorite(null)}>
              &times;
            </span>
            <p className="text-lg">{selectedFavorite}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Favorites;
